package j07_repeat;

public class ForOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 0; i < 10; i++) {
			System.out.println(i);
		}
		
		System.out.println();
		
		int i = 0;
		while(i < 10) {
			System.out.println(i);
			i++;
		}
		
		/*
		 * for문의 기본형태
		 * 
		 * for(초기치; 조건문; 증가치) {
		 * 		수행문장
		 * 		수행문장
		 * 		수행문장
		 * }
		 */
	}

}
