package j07_repeat;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("프로그램 시작");
		int i = 0; //초기문
		
		while(i < 5) { //조건문
			System.out.println(i);
			i++;
		}
		System.out.println("프로그램 종료");
		
		/*
		 * while 문의 기본구조
		 * while(조건문) {
		 * 		수행 문장
		 * 		수행 문장
		 * 		...
		 * }
		 * 
		 * */
	}

}
