package j07_repeat;

public class WhileTwo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		
		while(i <= 6) {
			System.out.println("박상훈" + i);
			i++;
		}
		
		/*
		 * 클린코딩의 조건은?
		 * 직관적으로 볼 수 있도록
		 * 0으로 시작해서 딱 도는 것까지
		 * 미만으로 되도록 쓴다.
		 *
		 */
	}
}
